mapper.select:
select follow_seq, customer_seq, following_seq
from follow
where customer_seq = #{customer_seq}

mapper.insert:
insert into
customer
(
customer_seq
,pw
, name
, email
, nick
, profileImg
, customer_type
)
values
(
customer_seq.nextval
,#{pw}
,#{name}
,#{email}
,#{name}
,#{profileImg}
,#{customer_type}
)

mapper.select:
select *
from follow
where customer_seq = #{customer_seq} and following_seq = #{following_seq}

mapper.update:
update customer set

 profileImg=#{profileImg}

where customer_seq like #{customer_seq}

		update customer set


			pw=#{pw},

  			 nick=#{nick}
		where customer_seq like #{customer_seq}
